        var className = 'ai-hover-effect-' + Math.floor(Math.random() * 10000);

        // 添加CSS类
        selectedComponent.addClass(className);

        // 添加相应的CSS样式到文档头部
        var styleTag = document.createElement('style');
        styleTag.innerHTML = "\n          .".concat(className, ":hover {\n            transform: translateY(-3px);\n            box-shadow: 0 5px 15px rgba(0,0,0,0.2);\n          }\n        ");
        document.head.appendChild(styleTag);
